"use strict";function lookAt(p,s){var t=$(".pupil"),e=$(".eye:first-child"),u=e.height(),r=e.width();t.each(function(t,e){var a=(e=$(e)).offset(),o=a.left+e.width()/2,n=a.top+e.height()/2,i=p-o,c=s-n,h=Math.atan(.01*i)/Math.PI*u,f=Math.atan(.01*c)/Math.PI*r;e.css("transform","translate3d("+h+"px, "+f+"px, 0)")})}$(document).ready(function(){$(".eye-pair").each(function(t,e){(e=$(e)).css({top:"30px",left:"30px"})}),$(document).mousemove(function(t){lookAt(t.pageX,t.pageY)})});